import React, { Component } from 'react';
import { MDBNavbar, MDBNavbarBrand, MDBNavbarNav, MDBNavbarToggler, MDBCollapse, MDBNavItem, MDBNavLink, MDBIcon } from 'mdbreact';
import { connect } from 'react-redux'
import { Redirect } from 'react-router-dom'

class TopNavigation extends Component {

    constructor(props) {
        super(props);
        this.deconnection = this.deconnection.bind(this)
        this.renderRedirect = this.renderRedirect.bind(this)
    }
    state = {
        collapse: true
    }

    renderRedirect = () => {
        if (localStorage.getItem('login') == 'false') {
            return <Redirect to='/login' />
        } else {
            console.log('test');
        }
    }

    deconnection(e) {
        console.log('local deconnection: ', localStorage.getItem('login'));
        const action = { type: "DECONNECT", value: e }
        this.props.dispatch(action)
    }

    onClick = () => {
        this.setState({
            collapse: !this.state.collapse,
        });
    }

    toggle = () => {
        this.setState({
            dropdownOpen: !this.state.dropdownOpen
        });
    }

    render() {
        return (
            <div className='container-fluid topbar-fixed'>
            {/* <MDBNavbar className="flexible-navbar" light expand="md" scrolling>
                <MDBNavbarBrand>
                    {this.renderRedirect()}
                    <strong>Bonjour {localStorage.getItem('user')}</strong>
                </MDBNavbarBrand>
                <MDBNavbarToggler onClick={this.onClick} />
                <MDBCollapse isOpen={this.state.collapse} navbar>

                    <MDBNavbarNav right>
                        <MDBNavItem onClick={() => {this.deconnection()}}>
                            <MDBNavLink rel="noopener noreferrer" className="nav-link Ripple-parent" href="/login">Deconnexion</MDBNavLink>
                        </MDBNavItem>
                    </MDBNavbarNav>
                </MDBCollapse>
            </MDBNavbar> */}

            <MDBNavbar id="Totalhome"
                        color="bg-primary"
                        dark
                        expand="md"
                        scrolling
                        className='position-fixed'
                        style={{ position: 'absolute', zIndex: '1000', float: 'left', width: '103.2%', marginLeft: '-3%' }}
                    >

                        <MDBNavbarBrand>
                            <img src="../images/logoPM.png" id="logo-header" alt="imagelogo" />
                        </MDBNavbarBrand>
                        <MDBNavbarToggler
                            image="../images/hamburger3.gif"
                            onClick={this.toggleCollapse("navbarCollapse")}
                        />
                        <MDBCollapse
                            id="navbarCollapse"
                            isOpen={this.state.collapseID}
                            navbar
                        >

                            <MDBNavbarNav right>
                                <MDBNavItem className='item'>
                                    <MDBNavLink to="#" className="item accueil">Services</MDBNavLink>
                                </MDBNavItem>
                                <MDBNavItem className='item'>
                                    <MDBDropdown>
                                        <MDBDropdownToggle nav caret>
                                            <span className="mr-2 item accueil">Dropdown</span>
                                        </MDBDropdownToggle>
                                        <MDBDropdownMenu>
                                            <MDBDropdownItem onClick={() => this.triage("Tete")}>Têtes de cortège</MDBDropdownItem>
                                            <MDBDropdownItem onClick={() => this.triage("Décoration")}>Salles de réception</MDBDropdownItem>
                                            <MDBDropdownItem href="#!">Décorations</MDBDropdownItem>
                                            <MDBDropdownItem href="#!">Services traiteurs</MDBDropdownItem>
                                            <MDBDropdownItem href="#!">Photographes/VIdéastes</MDBDropdownItem>
                                            <MDBDropdownItem divider />
                                            <MDBDropdownItem onClick={() => this.componentDidMount()}>Afficher tous</MDBDropdownItem>
                                        </MDBDropdownMenu>
                                    </MDBDropdown>
                                </MDBNavItem>
                                <MDBNavItem className='item'>
                                    <MDBNavLink to="/login" className="accueil">Connexion</MDBNavLink>
                                </MDBNavItem>

                            </MDBNavbarNav>
                        </MDBCollapse>

                    </MDBNavbar>
            </div>
        );
    }
}
const mapStateToProps = (state) => {
    return {
        client: state
    }
}
export default connect(mapStateToProps)(TopNavigation);
